body.script:
//
// Loading Screen
window.addEventListener('load', function() {
  const loadingScreen = document.getElementById('loadingScreen');
  setTimeout(() => {
    loadingScreen.style.opacity = '0';
    setTimeout(() => {
      loadingScreen.style.display = 'none';
    }, 500);
  }, 1000);
});

// Mobile Menu Toggle
const mobileMenuToggle = document.getElementById('mobileMenuToggle');
const navMenu = document.getElementById('navMenu');

mobileMenuToggle.addEventListener('click', function() {
  navMenu.classList.toggle('active');
  const icon = this.querySelector('i');
  icon.classList.toggle('fa-bars');
  icon.classList.toggle('fa-times');
});

// Services Tabs
const tabButtons = document.querySelectorAll('.tab-button');
const serviceCards = document.querySelectorAll('.service-card');

tabButtons.forEach(button => {
  button.addEventListener('click', function() {
    const category = this.dataset.tab;

    // Update active tab
    tabButtons.forEach(btn => btn.classList.remove('active'));
    this.classList.add('active');

    // Show/hide services
    serviceCards.forEach(card => {
      if (card.dataset.category === category) {
        card.style.display = 'block';
      } else {
        card.style.display = 'none';
      }
    });
  });
});

// Floating Contact Menu
const contactTrigger = document.getElementById('contactTrigger');
const contactMenu = document.getElementById('contactMenu');

contactTrigger.addEventListener('click', function() {
  contactMenu.classList.toggle('active');
});

// Close contact menu when clicking outside
document.addEventListener('click', function(e) {
  if (!contactTrigger.contains(e.target) && !contactMenu.contains(e.target)) {
    contactMenu.classList.remove('active');
  }
});

// Back to Top Button
const backToTop = document.getElementById('backToTop');

window.addEventListener('scroll', function() {
  if (window.scrollY > 300) {
    backToTop.classList.add('visible');
  } else {
    backToTop.classList.remove('visible');
  }

  // Header background change
  const header = document.getElementById('header');
  if (window.scrollY > 100) {
    header.style.background = 'rgba(255, 255, 255, 0.95)';
    header.style.backdropFilter = 'blur(10px)';
  } else {
    header.style.background = 'var(--white)';
    header.style.backdropFilter = 'none';
  }
});

backToTop.addEventListener('click', function() {
  window.scrollTo({
    top: 0,
    behavior: 'smooth'
  });
});

// Smooth Scrolling for Navigation Links
document.querySelectorAll('a[href^="#"]').forEach(anchor => {
  anchor.addEventListener('click', function (e) {
    e.preventDefault();
    const target = document.querySelector(this.getAttribute('href'));
    if (target) {
      const headerHeight = document.getElementById('header').offsetHeight;
      const targetPosition = target.offsetTop - headerHeight;

      window.scrollTo({
        top: targetPosition,
        behavior: 'smooth'
      });

      // Close mobile menu if open
      navMenu.classList.remove('active');
      mobileMenuToggle.querySelector('i').classList.add('fa-bars');
      mobileMenuToggle.querySelector('i').classList.remove('fa-times');
    }
  });
});

// Gallery Lightbox
const galleryItems = document.querySelectorAll('.gallery-item img');
galleryItems.forEach((img, index) => {
  img.setAttribute('data-lightbox', 'gallery');
  img.setAttribute('data-title', `Salon Menissa - Bild ${index + 1}`);
});

// Cookie Banner
function showCookieBanner() {
  const cookieBanner = document.getElementById('cookieBanner');
  if (!localStorage.getItem('cookieConsent')) {
    setTimeout(() => {
      cookieBanner.classList.add('show');
    }, 2000);
  }
}

function acceptCookies() {
  localStorage.setItem('cookieConsent', 'accepted');
  document.getElementById('cookieBanner').classList.remove('show');
}

function declineCookies() {
  localStorage.setItem('cookieConsent', 'declined');
  document.getElementById('cookieBanner').classList.remove('show');
}

// Show cookie banner on page load
showCookieBanner();

// Animation on Scroll
const observerOptions = {
  threshold: 0.1,
  rootMargin: '0px 0px -50px 0px'
};

const observer = new IntersectionObserver(function(entries) {
  entries.forEach(entry => {
    if (entry.isIntersecting) {
      entry.target.style.opacity = '1';
      entry.target.style.transform = 'translateY(0)';
    }
  });
}, observerOptions);

// Observe elements for animation
document.querySelectorAll('.service-card, .blog-card, .gallery-item').forEach(el => {
  el.style.opacity = '0';
  el.style.transform = 'translateY(30px)';
  el.style.transition = 'opacity 0.6s ease, transform 0.6s ease';
  observer.observe(el);
});
